import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import EmployeeSevice from '../Service/EmployeeSevice'
import Swal from 'sweetalert2';
import ReactPaginate from 'react-paginate';

const EmployeeList = () => {

    const [employees, setEmployees] = useState([])
 

    useEffect(() => {

        getAllEmployees();
    }, [])

    const getAllEmployees = () => {
        EmployeeSevice.getAllEmployees().then((response) => {
            setEmployees(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    }

    const deleteEmployee = (Id) => {
        Swal.fire({
            icon: 'warning',
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'No, cancel!',
        }).
        then((response) => {
            if(response.value){
                EmployeeSevice.deleteEmployee(Id); 
                Swal.fire({
                    icon: 'success',
                    title: 'Deleted!',
                    text: `${employees.name}'s data has been deleted.`,
                    showConfirmButton: false,
                    timer: 3000,
                });
                getAllEmployees();
               
            }   
        }).catch((error) => {
            console.log(error)
        })
    }

    return (
        <div className = "container">
            <h2 className = "text-center"> List Employees </h2>
            <Link to="/add-employee" className = "btn btn-primary mb-2" > Add Employee </Link> &nbsp; &nbsp;
            
            <Link to="/show-department" className = "btn btn-primary mb-2" > Department </Link>

            <table className="table table-bordered table-striped">
                <thead>
                    {/* <th> Id </th> */}
                    <th> Name </th>
                    <th> Age </th>
                    <th> Email </th>
                    <th> Joining date </th>
                    <th> Salary </th>
                    {/* <th> Department Id</th> */}
                    <th> Department Name </th>
                    
                    <th> Actions </th>
                </thead>
                <tbody>
                    {
                        employees.map(
                            employee =>
                            
                            <tr> 
                                {/* <td> {employee.id} </td> */}
                                <td> {employee.name} </td>
                                <td>{employee.age}</td>
                                <td>{employee.email}</td>
                                <td>{employee.JoiningDate}</td>
                                <td>{employee.salary}</td>
                                {/* <td>{employee.department.deptId}</td> */}
                                <td>{employee.department.deptName}</td>

                                
                                <td>
                                    <Link className="btn btn-info" to={`/edit-employee/${employee.id}`} >Update</Link>
                                    <button className = "btn btn-danger" onClick = {() => deleteEmployee(employee.id)}
                                    style = {{marginLeft:"10px"}}> Delete</button>
                                </td>
                            </tr>
                        )
                    }
                </tbody>
            </table>
            <ReactPaginate
         previousLabel={'Previous'}
         nextLabel={'Next'}
         breakLabel={'...'}
         pageCount={10}
         marginPagesDisplayed={3}
         pageRangeDisplayed={3}
         onPageChange={ 'handlePageClick'}
         containerClassName={'pagination justify-content-center'}
         pageClassName={'page-item'}
         pageLinkClassName={'page-link'}
         previousClassName={'page-item'}
         previousLinkClassName={'page-link'}
         nextClassName = {'page-item'}
         nextLinkClassName={'page-link'}
         breakClassName={'page-item'}
         breakLinkClassName={'page-link'}
         activeClassName={'active'}
         >
         </ReactPaginate>
        </div>
        
    )
}

export default EmployeeList
