import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import EmployeeSevice from '../Service/EmployeeSevice'
import Swal from 'sweetalert2'
import Pagination from "@material-ui/lab/Pagination";

const EmployeeList = () => { 
    const [page, setPage] = useState(1);
    


    const getAllEmployees = () => {
        EmployeeSevice.getAllEmployees().then((response) => {
            setEmployees(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    }

    const deleteEmployee = (Id) => {

        Swal.fire({

            icon: 'warning',

            title: 'Are you sure?',

            text: "You won't be able to revert this!",

            showCancelButton: true,

            confirmButtonText: 'Yes, delete it!',

            cancelButtonText: 'No, cancel!',

        }).

        then((response) => {

            if(response.value){

                EmployeeSevice.deleteEmployee(Id);

                Swal.fire({

                    icon: 'success',

                    title: 'Deleted!',

                    text: `${employees.name}'s data has been deleted.`,

                    showConfirmButton: false,

                    timer: 3000,

                });

                getAllEmployees();

               

            }  

        }).catch((error) => {

            console.log(error)

        })

    }

    return (
        <div className = "container">
            <h2 className = "text-center"> List Employees </h2>
            <Link to="/add-employee" className = "btn btn-primary mb-2" > Add Employee </Link>
            <table className="table table-bordered table-striped">
                <thead>
                    <th> Id </th>
                    <th> Age </th>
                    <th> Name </th>
                    <th> Salary </th>
                    <th> Email </th>
                    <th> Joining date </th>
                    <th> Department Id</th>
                    <th> Department Name </th>
                    
                    <th> Actions </th>
                </thead>
                <tbody>
                    {
                        employees.map(
                            employee =>
                            
                            <tr> 
                                <td> {employee.id} </td>
                                <td> {employee.age} </td>
                                <td>{employee.name}</td>
                                <td>{employee.salary}</td>
                                <td>{employee.email}</td>
                                <td>{employee.JoiningDate}</td>
                                <td>{employee.department.deptId}</td>
                                <td>{employee.department.deptName}</td>

                                
                                <td>
                                    <Link className="btn btn-info" to={`/edit-employee/${employee.id}`} >Update</Link>
                                    <button className = "btn btn-danger" onClick = {() => deleteEmployee(employee.id)}
                                    style = {{marginLeft:"10px"}}> Delete</button>
                                </td>
                            </tr>
                        )
                    }
                </tbody>
            </table>
            <div className="mt-3">

